@file:Suppress("OPT_IN_USAGE")

package naksha.model

import naksha.base.Int64
import kotlin.js.JsExport

/**
 * The metadata about a [Row] generated by the storage, read-only for the client.
 */
@JsExport
data class Metadata(
    val updatedAt: Int64,
    val createdAt: Int64,
    val authorTs: Int64,
    val txnNext: Int64?,
    val txn: Int64,
    val ptxn: Int64?,
    val uid: Int,
    val puid: Int,
    val fnva1: Int,
    val version: Int,
    val geoGrid: Int,
    val flags: Int,
    val origin: String?,
    val appId: String,
    val author: String?,
    val type: String?,
    val id: String
)